package hospital; public class Room { private String id; private String description; public Room(String id, String description){ this.id = id; this.description = description; } public String getId(){ return id; } public String getDescription(){ return description; } public String toCsv(){ return id+"|"+description; } public static Room fromCsv(String csv){ String[] p = csv.split("\\|", -1); return new Room(p[0], p.length>1?p[1]:""); } @Override public String toString(){ return String.format("Quarto %s | %s", id, description); } }